import org.junit.Rule
import org.junit.Test

class HomeScreenTestWithTestTag {

 @get:Rule
 val composeTestRule = createComposeRule()

 @Test
 fun homeScreen_ShouldDisplayNotes() {
 composeTestRule.setContent {
 UITestComposeMaestroTheme {
 HomeScreen()
 }
 }

 val notes = buildSampleNotes()
 val firstNote = notes[0]

 composeTestRule.onNodeWithTag(HomeComponentKey.NOTE_TITLE.plus(firstNote.id)).assertExists()
 }

 @Test
 fun homeScreen_ShouldDisplayNoteDetailsOnClick() {
 composeTestRule.setContent {
 UITestComposeMaestroTheme {
 HomeScreen()
 }
 }

 val notes = buildSampleNotes()
 val firstNote = notes[0]

 composeTestRule.onNodeWithText(firstNote.title).performClick()

 composeTestRule.onAllNodesWithText(firstNote.title).onLast().assertIsDisplayed()
 composeTestRule.onAllNodesWithText(firstNote.content).onLast().assertIsDisplayed()
 composeTestRule.onNodeWithText("Close").assertIsDisplayed()
 }

 @Test
 fun homeScreen_ShouldDisplayLastNoteDetailsOnClick() {
 composeTestRule.setContent {
 UITestComposeMaestroTheme {
 HomeScreen()
 }
 }

 val notes = buildSampleNotes()
 val lastNote = notes.last()

 composeTestRule.onNode(hasScrollToNodeAction()).performScrollToNode(hasText(lastNote.title))
 composeTestRule.onNodeWithText(lastNote.title).performClick()
 composeTestRule.onAllNodesWithText(lastNote.title).onLast().assertIsDisplayed()
 composeTestRule.onAllNodesWithText(lastNote.content).onLast().assertIsDisplayed()
 composeTestRule.onNodeWithText("Close").assertIsDisplayed()
 }

 @Test
 fun homeScreen_ShouldCloseNoteDetailsOnCloseButtonClick() {
 composeTestRule.setContent {
 UITestComposeMaestroTheme {
 HomeScreen()
 }
 }

 val notes = buildSampleNotes()
 val firstNote = notes[0]

 composeTestRule.onNodeWithText(firstNote.title).performClick()

 composeTestRule.onNodeWithText("Close").performClick()
 composeTestRule.onNodeWithText("Close").assertIsNotDisplayed()
 }
}
